UPDATE `jos_emundus_setup_tags` SET `request`='php| $db = JFactory::getDbo(); $query = $db->getQuery(true); $query->select("p.label")->from("jos_emundus_campaign_candidature AS cc")->leftJoin("jos_emundus_setup_campaigns AS c ON cc.campaign_id = c.id")->leftJoin("jos_emundus_setup_profiles AS p ON p.id = c.profile_id")->where("cc.fnum LIKE ".$db->quote("[FNUM]")); $db->setQuery($query); return $db->loadResult(); ' WHERE `tag` LIKE 'USER_PROFILE';