INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`, `email_tmpl`) VALUES (NULL, 'registration_email', 'Activez votre compte.', NULL, '<p>Bonjour [USER_NAME],</p>\r\n<p>Merci de vous être inscrit sur [SITE_NAME].</p>\r\n<p>Votre compte a été créé et doit être activé avant que vous puissiez l\'utiliser. </p>\r\n<p>Pour l\'activer, cliquez sur le lien ci-dessous </p>\r\n<p><a type=\"button\" href=\"[ACTIVATION_URL]\">CONFIRMER COMPTE</a> </p>\r\n<p>Après activation vous pourrez vous connecter à : [BASE_URL] </br> En utilisant votre identifiant : [USER_LOGIN]\n</p>', NULL, '2', '1', '1')
