INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`, `email_tmpl`, `letter_attachment`, `candidate_attachment`) VALUES (NULL, 'request_rgpd', 'Information Request Submitted', '', '<p>A new information request has been submitted by [EMAIL].</p>', '', '2', '1', '1', NULL, NULL);
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`, `email_tmpl`, `letter_attachment`, `candidate_attachment`) VALUES (NULL, 'confirm_rgpd', 'Information Request Confirmed By User', '', 'User [EMAIL] has confirmed their information request.', NULL, '2', '1', '1', NULL, NULL);
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`, `email_tmpl`, `letter_attachment`, `candidate_attachment`) VALUES (NULL, 'requestuser_rgpd', 'Information [TYPEREQUEST] at [SITENAME]', '', '<p>Someone has created a request to [REQUEST] all personal information related to this email address at [URL]. As a security measure, you must confirm that this is a valid request for your personal information to be removed from this website.</p><p>In order to confirm this request, you can complete one of the following tasks:</p><p>1. Visit the following URL:</p><p> [TOKENURL]</p><p>2. Copy your token from this email, visit the referenced URL, and paste your token into the form.</p><p>URL: [FORMURL]</p><p>Token: [TOKEN]</p><p>Please note that this token is only valid for 24 hours from the time this email was sent.</p>', '', '2', '1', '1', NULL, NULL);