INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`) VALUES (NULL, 'booking_created_user', 'Confirmation de date et horaire d\'entretien.', NULL, '<H2> Bonjour [USER_NAME] </H2>\r\n\r\n<p> Votre reservation d\'entretien pour et admis dans le programme [PROGRAM] a bien été pris en compte. </p>\r\n\r\n<p> Rendez vous le [EVENT_DATE] à [EVENT_TIME]. </p>\r\n', NULL, '2', '1');
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`) VALUES (NULL, 'booking_created_recipient', 'Réservation d\'entretien: [USER_NAME]', NULL, '<H2> Bonjour [RECIPIENT_NAME]! </H2>\r\n\r\n<p> Le candidat [USER_NAME] a réservé un entretien le [EVENT_DATE] à [EVENT_TIME]</p>\r\n\r\n<p> Programme: [PROGRAM] </p>', NULL, '2', '1');
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`) VALUES (NULL, 'booking_deleted_user', 'Confirmation d\'annulation d\'entretien.', NULL, '<H2> Bonjour [USER_NAME] </H2>\r\n\r\n<p> Votre annulation d\'entretien à [EVENT_TIME] le [EVENT_DATE] pour le programme [PROGRAM] a bien été pris en compte. </p>\r\n\r\n<p> Vous pouvez toujours réserver un autre rendez-vous. </p>\r\n', NULL, '2', '1');
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`) VALUES (NULL, 'booking_deleted_recipient', 'Annulation d\'entretien: [USER_NAME]', NULL, '<H2> Bonjour [RECIPIENT_NAME] </H2>\r\n\r\n<p> Le candidat [USER_NAME] a annulé un entretien le [EVENT_DATE] à [EVENT_TIME]</p>\r\n\r\n<p> Programme: [PROGRAM] </p>', NULL, '2', '1')

SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

CREATE TABLE `jos_emundus_email_templates` (
  `id` int(11) NOT NULL,
  `date_time` datetime DEFAULT NULL,
  `lbl` varchar(255) DEFAULT NULL,
  `Template` text,
  `type` text,
  `published` int(1) DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


ALTER TABLE `jos_emundus_email_templates`
  ADD PRIMARY KEY (`id`);


ALTER TABLE `jos_emundus_email_templates`
  MODIFY `id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=2;

ALTER TABLE `jos_emundus_setup_emails` ADD `email_tmpl` INT(11) NOT NULL DEFAULT '1';

INSERT INTO `jos_emundus_email_templates` (`id`, `date_time`, `lbl`, `Template`, `type`, `published`) VALUES (NULL, '2017-10-09 12:03:29', 'default', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n <meta charset=\"utf-8\">\r\n <meta name=\"viewport\" content=\"width=device-width\">\r\n <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n <meta name=\"x-apple-disable-message-reformatting\">\r\n <title>[EMAIL_SUBJECT]</title>\r\n\r\n <style>\r\n\r\n\r\n html,\r\n body {\r\n margin: 0 auto !important;\r\n padding: 0 !important;\r\n height: 100% !important;\r\n width: 100% !important;\r\n }\r\n\r\n * {\r\n -ms-text-size-adjust: 100%;\r\n -webkit-text-size-adjust: 100%;\r\n }\r\n\r\n div[style*=\"margin: 16px 0\"] {\r\n margin:0 !important;\r\n }\r\n\r\n table,\r\n td {\r\n mso-table-lspace: 0pt !important;\r\n mso-table-rspace: 0pt !important;\r\n }\r\n\r\n table {\r\n border-spacing: 0 !important;\r\n border-collapse: collapse !important;\r\n table-layout: fixed !important;\r\n margin: 0 auto !important;\r\n }\r\n table table table {\r\n table-layout: auto;\r\n }\r\n\r\n img {\r\n -ms-interpolation-mode:bicubic;\r\n }\r\n\r\n *[x-apple-data-detectors] {\r\n color: inherit !important;\r\n text-decoration: none !important;\r\n }\r\n\r\n .x-gmail-data-detectors,\r\n .x-gmail-data-detectors *,\r\n .aBn {\r\n border-bottom: 0 !important;\r\n cursor: default !important;\r\n }\r\n\r\n .a6S {\r\n display: none !important;\r\n opacity: 0.01 !important;\r\n }\r\n img.g-img + div {\r\n display:none !important;\r\n }\r\n\r\n .button-link {\r\n text-decoration: none !important;\r\n }\r\n\r\n @media only screen and (min-device-width: 375px) and (max-device-width: 413px) {\r\n .email-container {\r\n min-width: 375px !important;\r\n }\r\n }\r\n\r\n </style>\r\n\r\n <!--[if gte mso 9]>\r\n <xml>\r\n <o:OfficeDocumentSettings>\r\n <o:AllowPNG/>\r\n <o:PixelsPerInch>96</o:PixelsPerInch>\r\n </o:OfficeDocumentSettings>\r\n </xml>\r\n <![endif]-->\r\n\r\n <style>\r\n\r\n .button-td,\r\n .button-a {\r\n transition: all 100ms ease-in;\r\n }\r\n .button-td:hover,\r\n .button-a:hover {\r\n background: #EA4503 !important;\r\n border-color: #EA4503 !important;\r\n color: #FFFFFF;\r\n }\r\n\r\n @media screen and (max-width: 600px) {\r\n .email-container p {\r\n font-size: 17px !important;\r\n line-height: 22px !important;\r\n }\r\n \r\n }\r\n\r\n </style>\r\n\r\n</head>\r\n<body width=\"100%\" bgcolor=\"#CCCCCC\" style=\"margin: 0; mso-line-height-rule: exactly;\">\r\n <center style=\"width: 100%; background: #CCCCCC; text-align: left;\">\r\n\r\n <div style=\"display:none;font-size:1px;line-height:1px;max-height:0px;max-width:0px;opacity:0;overflow:hidden;mso-hide:all;font-family: sans-serif;\">\r\n (IMPORTANT) [EMAIL_SUBJECT]\r\n </div>\r\n\r\n <div style=\"max-width: 600px; margin: auto;\" class=\"email-container\">\r\n <table role=\"presentation\" aria-hidden=\"true\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"600\" align=\"center\">\r\n <tr>\r\n <td>\r\n\r\n <table role=\"presentation\" aria-hidden=\"true\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\" width=\"100%\" style=\"max-width: 600px;\">\r\n <tr>\r\n <td style=\"padding: 20px 0; text-align: center\">\r\n <img src=\"https://emundus.fr/images/logo.png\" aria-hidden=\"true\" width=\"200\" height=\"50\" alt=\"emundus_logo\" border=\"0\" style=\"height: auto; background: #cccccc; font-family: sans-serif; font-size: 15px; line-height: 20px; color: #555555;\">\r\n </td>\r\n </tr>\r\n </table>\r\n\r\n <table role=\"presentation\" aria-hidden=\"true\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\" width=\"100%\" style=\"max-width: 600px;\">\r\n\r\n <tr>\r\n <td bgcolor=\"#ffffff\">\r\n <table role=\"presentation\" aria-hidden=\"true\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n <tr>\r\n <td style=\"padding: 40px; font-family: sans-serif; font-size: 15px; line-height: 20px; color: #555555;\">\r\n [MESSAGE_BODY]\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n\r\n <tr>\r\n <td height=\"40\" style=\"font-size: 0; line-height: 0;\">\r\n &nbsp;\r\n </td>\r\n </tr>\r\n\r\n </table>\r\n\r\n <table role=\"presentation\" aria-hidden=\"true\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\" width=\"100%\" style=\"max-width: 680px;\">\r\n <tr>\r\n <td style=\"padding: 40px 10px;width: 100%;font-size: 12px; font-family: sans-serif; line-height:18px; text-align: center; color: #888888;\" class=\"x-gmail-data-detectors\">\r\n <br><br>\r\n eMundus<br>1 Rue Fleming, La Rochelle, 17000 FR<br>+33(0)5.46.30.78.27\r\n <br><br>\r\n </td>\r\n </tr>\r\n </table>\r\n\r\n </div>\r\n </center>\r\n</body>\r\n</html>', '1', '1');
