INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`, `type`, `published`, `email_tmpl`) VALUES (NULL, 'new_ldap_account', 'Bienvenue sur la plateforme candidature eMundus', 'contact@emundus.fr', '<p>Bonjour,</p>\r\n<p>Un nouveau compte utilisateur a été créé pour vous via l\'anuaire LDAP de votre institution. Vous pouvez donc vous connecter avec votre mot de passe habituel:</p>\r\n<p><a href=\"[SITE_URL]\">[SITE_URL]</a>.</p>\r\n<p>Vous trouverez vos informations de connexion à la fin de ce message.</p>\r\n<p>Cordialement,<br />L\'équipe d\'administration du site</p>\r\n<p>---<br />A new account has been created for you on the application platform using your LDAP credentials: <a href=\"[SITE_URL]\">[SITE_URL]</a><br /><br />Kind Regards, <br />Website Administration Team</p>\r\n<hr />\r\n<p>Your username : [USERNAME] <br /><br />Your email : [EMAIL]</p>', 'eMundus', '1', '1', '1');